mapper.select:
SELECT NUM, TITLE, WRITER, P_NUM, CONTENT, ATTACH as attachPath
FROM   FREEBOARD
WHERE  NUM = #{rnum}

mapper.update:
UPDATE FREEBOARD
SET    TITLE = #{title}, CONTENT = #{content}, ATTACH = #{attachPath}
WHERE  NUM = #{num}

